INSERT INTO `jp_NewsAndEvents` (`ID`, `PostedBy`, `DatePosted`, `Location`, `Title`, `Details`, `Image`) VALUES
(2, 'ricky.ogayon', '2013年05月01日', '日本', '日本５周年記念パーティー', 'ITCS Japan 株式会社は今月、5周年記念パーティー主催致します！長年にわたってお世話になっている皆様にお会いできます事を楽しみにしております！', ''),
(5, 'Ricky Ogayon', '2016年07月6日', '香港, シンガポール, & 日本', 'IT規制アドバイザリーサービス', 'ITCSは、サイバーセキュリティに関して、現地規制当（SFC/MAS/FSA）監査に対応するための新しいサービスを開始しました。弊社のIT規制アドバイザリーサービスはライセンス取得金融機関に対し、現地の規制に準拠したITコンプライアンスに取り組むサービスを提供します。\r\n\r\n<br><br>\r\n\r\n                            <a class=\'btnlink btn btn-info btn-sm box-shadow--2dp\' href=\'services.php?ID=6\' style=\'font-size: 12px\'> <i class=\'fa fa-folder-open\'></i> <strong>詳細を見る</strong> </a>', ''),
(6, 'Ricky Ogayon', '2016年07月28日 ', '日本', 'サマーカクテル', '当社ITCSは7月28日(木)にサマーカクテルパーティを開催致しました。丸の内ビル内にあるフレンチレストラン Monna Lisaで行われ、大成功を収めました。', ''),
(7, 'Ricky Ogayon', '2017年01月25日', '香港', '毎年恒例のAIMA APACフォーラム', 'ITCSグループは、光栄にも1月19日に香港コンベンション＆エキシビジョンセンターで開催されたAlternative Investment Management Association (AIMA) APAC フォーラム2017 のプラチナスポンサーを務めました。このフォーラムはAIMA のアジア圏内主要の協議会で、アジア最大級のイベントです。そこで当ITCSグループの創業者兼経営者のDaniel Kolbeが、サイバーセキュリティー分野の主力スピーカーとして参加させていただく栄誉を受けました。皆さま、AIMA APACフォーラム又は弊社の無料サイバーセキュリティー審査にご興味がございましたら<a href=\"mailto:info@itcs-group.com\">info@itcs-group.com</a>までお気軽にお問合せください。', ''),
(8, 'Aleksandr Chernov', '2018年11月30日', '日本', '年次イベント', '2018年11月に香港、シンガポール、東京でクライアント・カクテルパーティを開催致しました。\r\nとても素晴らしいパーティになり、参加していただきましたクライアント様方、当社スタッフ達に大変感謝しております。', '');
INSERT INTO `jp_Service` (`ID`, `SubMenuID`, `Title`, `Image`, `Introduction`, `Details`, `URL`) VALUES
(1, 1, 'ITコンサルティングとプロジェクトマネジメント', 'images/service1.jpg', 'ITCSグループの専門は、お客様企業のIT環境に変化をもたらすことです。お客様が当社にご依頼を検討されるタイミングは多くの場合、合併、買収、イン／アウトソーシング計画時、大規模な設備投資などの、大きなイベントに伴ってプロジェクトが発生した時です。', 'ITCSグループの専門は、お客様企業のIT環境に変化をもたらすことです。お客様が当社にご依頼を検討されるタイミングは多くの場合、合併、買収、イン／アウトソーシング計画時、大規模な設備投資などの、大きなイベントに伴ってプロジェクトが発生した時です。\r\n\r\n</br> </br><strong>合併や買収</strong>\r\n\r\n</br> </br>合併・買収は、通常の業務では発生しないような問題が発生するため、IT組織にとって大きな課題となります。テクノロジーと組織の両方の分野でスケールの大きな変更を成功させるには、具体的な経験と知識、そして文化的な配慮をもって臨む必要があります。\r\n\r\n</br> </br>当社はITビジネスおよびテクノロジーのマネジメントの経験を有しており、以下のサービスを提供しています：\r\n<ul class=\'list-style\'> \r\n<LI>リソースプランニング</LI>\r\n<LI>インソーシング／アウトソーシング管理</LI>\r\n<LI>契約の再交渉や更新</li>\r\n<LI>ITとビジネスとの連携 </LI>\r\n<LI>IT戦略</ li>\r\n<LI>変更や移行のプランニング</li>\r\n<LI>プログラムやプロジェクトの管理</li>\r\n<LI>ITインフラ統合サービス</LI>\r\n<LI>アプリケーション移行</li>\r\n<LI>統合と整理</li>\r\n</UL>\r\n\r\n</br><strong>企業不動産プロジェクト</strong>\r\n</br></br> ITCSグループは、不動産プロジェクトに関連するITインフラについて、これを正しく規定し提供することが、どれほど重要なことかを十分に理解しています。当社はプロジェクトのライフサイクルの最初から最後まで責任を持って関わり、企業不動産、設備管理、建設プロジェクトマネジメント、積算士、インテリアデザイナー、機械エンジニアリング、電気工学エンジニアリングの各部門と共に作業を行うことで、そのビジネスのIT要件を正しく捉えます。総合的に建設計画にIT要件を盛り込み、各作業間のインターフェースで確実にコミュニケーションがとれるようにし、効率的なマネジメントを提供します。\r\n<br><br>\r\n当社の真価がもっとも発揮されるのは、トレーディングフロアやデータセンター、マシンルームなど、テクノロジーのインフラ比率の高い企業不動産プロジェクトです。\r\n当社は、注目度の高い大規模なオフィス移転やリノベーションの設計、調達、導入および移行の管理について広範囲な経験を持っています。当社のコンサルタントは、数千人のユーザーや複数のロケーション、多様な企業文化に関わってきた経験を持っています。\r\n\r\n</br></br>当社のサービス：\r\n<ul class=\'list-style\'> \r\n<LI>現場選定およびデューデリジェンス</li>\r\n<LI>IT戦略立案</li>\r\n<LI>要件の明確化および調達</li>\r\n<LI>導入と移行の管理</li>\r\n<LI>MACおよびチャーン率</li>\r\n</UL>\r\n\r\n</br><strong>データセンターサービス</strong>\r\n</br></br>ITCSグループは、中規模から大規模の企業様に対し、データセンターの建設及び運営に関するサービスを提供します。データセンターを建設し運用するには、多くの資本と運用コストがかかります。効率的な投資収益率を確保しつつ、ビジネスに要求される高いサービスレベルを常に満たすには、専門的な知識が必要となります。\r\n<br><br>\r\n当社コンサルタントは機械や電気部門の設計者やエンジニアと協働することで、適切な電源、スペース、空調を確保し、さらにテクノロジースペースの効率性や、信頼性、拡張性の最適化が確実に行われるようにします。\r\n\r\n</br></br>当社のサービス：\r\n<ul class=\'list-style\'> \r\n<LI>データセンター戦略</li>\r\n<LI>監査および在庫管理</li>\r\n<LI>現場評価、デューデリジェンス、選定</li>\r\n<LI>施設およびM&E設計を盛り込んだIT要件の仕様</li>\r\n<LI>ITインフラ設計および配備管理</li>\r\n<LI>ネットワーク、ストレージ、コアシステムの導入</li>\r\n<LI>サーバー統合および仮想化</li>\r\n<LI>アプリケーション移行</li>\r\n<LI>データセンター運営のアウトソーシング</li>\r\n</UL>\r\n\r\n</br><strong>テクノロジー投資</strong>\r\n</br></br>ITCSグループはベンダーインディペンデントです。独立性があることで、当社が提供するアドバイスは常にお客様にとって最良のものと確証しており、アドバイザーとして長期的な信頼関係を築きあげる土台となっています。\r\n\r\n</br></br>独立した第三者という立場から、当社はガバナンスと責任を有し、公平さを期したテクノロジーの選定や調達を頻繁に行っています。\r\n\r\n</br></br>当社のサービス：\r\n<ul class=\'list-style\'> \r\n<LI>ビジネス要件およびIT要件の明確化</li>\r\n<LI>ベンダーに左右されないテクノロジー戦略とその明確化</li>\r\n<LI>市場評価およびベンチマーキング</li>\r\n<LI>独立したRFIおよびRFPマネジメント</LI>\r\n<LI>サプライヤ評価および契約交渉</li>\r\n</UL>', ''),
(2, 2, 'マネージドサービスと機動的な人員配置', 'images/service2.jpeg', 'ITCSグループでは、プロジェクト管理、アプリケーション開発、インフラサービスの分野で、高度な技術を持つ経験豊かなリソースを提供しています', 'ITCSグループでは、プロジェクト管理、アプリケーション開発、インフラサービスの分野で、高度な技術を持つ経験豊かなリソースを提供しています。\r\n\r\n<br><br>\r\n\r\nお客様の調達ニーズに応じて、ITCSグループでは以下のリソースを提供しています。\r\n\r\n<br>\r\n\r\n<UL>\r\n<LI>固定価格のマネージドサービスあるいは外部委託サービス</LI>\r\n<LI>プロジェクトベースのチーム</LI>\r\n<LI>臨時の個別コンサルタント</LI>\r\n<LI>人材紹介</LI>\r\n<LI>暫定的なマネジメント</LI>\r\n</UL>\r\n\r\n<br>\r\n\r\nITCSグループでは、以下のリソースカテゴリーでスタッフに関するソリューションを提供しています：\r\n\r\n<br><br>\r\n\r\n\r\n<strong>ITプログラムとプロジェクト管理</strong>\r\n<UL>\r\n<LI>プログラムおよびプロジェクトマネージャ</LI>\r\n<LI>PMO管理および運営管理</LI>\r\n<LI>サービスデリバリーマネジメント</LI>\r\n<LI>ビジネスアナリスト</LI>\r\n</UL>\r\n\r\n<strong>ITインフラサービス</strong>\r\n<UL>\r\n<LI>Wintelサポート、エンジニアリング、システム管理</LI>\r\n<LI>UNIXサポート、エンジニアリング、システム管理</LI>\r\n<LI>データベースアナリストおよび運営管理</LI>\r\n<LI>仮想化エンジニアリング</LI>\r\n<LI>ストレージエンジニアリングおよびサポート</LI>\r\n<LI>データセンター管理</LI>\r\n</UL>\r\n\r\n<strong>トレーディングのインフラと市場データ</strong>\r\n<UL>\r\n<LI>市場データアナリスト</LI>\r\n<LI>市場データサポートおよびマネジメント</LI>\r\n<LI>市場トレーディングのインフラ</LI>\r\n<LI>メッセージングおよびミドルウェア</LI>\r\n</UL>\r\n\r\n<strong>情報セキュリティ</strong>\r\n<UL>\r\n<LI>情報リスク</LI>\r\n<LI>情報セキュリティ担当</LI>\r\n<LI>情報セキュリティアナリスト</LI>\r\n</UL>\r\n\r\n<strong>エンドユーザーとデスクトップのサポート</strong>\r\n<UL>\r\n<LI>デスクトップサポート</LI>\r\n<LI>アプリケーションのパッケージ化と展開</LI>\r\n<LI>MACおよびチャーンスタッフ</LI>\r\n</UL>\r\n\r\n<strong>音声とネットワーク</strong>\r\n<UL>\r\n<LI>ネットワーク設計、サポート、エンジニアリング</LI>\r\n<LI>ボイス設計、エンジニアリング、サポート</LI>\r\n<LI>ディーリングシステムおよびタレット</LI>\r\n</UL>\r\n\r\n<strong>プログラム＆アプリケーション開発およびサポート</strong>\r\n<UL>\r\n<LI>JAVA開発者</LI>\r\n<LI>ウェブ開発者（HTML、PHP、JQuery、.NET、J2EE、VBA）</LI>\r\n<LI>アナリストプログラマー</LI>\r\n<LI>ビジネスアナリスト</LI>\r\n<LI>Fedessa開発者＆サポート</LI>\r\n</UL>\r\n\r\n<br>\r\n\r\n人員の確保に関して、社内の正社員では必ずしも最良の結果が得られないような場合や、人員追加の承認が下りないような場合にも、ITCSグループはお客様の個々のニーズに合わせてソリューションを提供することができます。また当社は地域一帯に経験豊かなリソースを持っているため、緊急を要するときでも迅速に対応することができ、一時的なリソース不足も解決できるようになっています。必要な時だけあるいは非常勤ベースで専門知識を提供することも可能です。', ''),
(3, 3, 'アプリケーション開発とオフショアリングに関するアドバイス', 'images/service3.jpeg', 'ITCSグループでは、アジア太平洋地域全域のグローバル企業様に対し、アプリケーション開発サービス、プログラミングリソース、アウトソーシングを提供しています', 'ITCSグループでは、アジア太平洋地域全域のグローバル企業様に対し、アプリケーション開発サービス、プログラミングリソース、アウトソーシングを提供しています。\r\n\r\n</br></br>一度限りのニッチなアプリケーション開発プロジェクトから、継続的に行われる開発、統合、強化サービスまで、様々なセクターに対応することが可能です。\r\n\r\n</br></br>当社のサービスが含まれます：\r\n<ul class=\'list-style\'>\r\n<li>フロントおよびバックオフィスのプラットフォーム開発</li>\r\n<li>社内ソフトウェアのコンサルティングおよびリソース提供</li>\r\n<li>アプリケーション開発のアウトソーシング</li>\r\n<li>アプリケーションサポートおよびマネジメントサービス</li>\r\n<li>オフショアリングに関するアドバイスおよび移行サービス</li>\r\n</ul>', ''),
(4, 4, 'ITサポート', 'images/service1.jpg', '', 'ITCSは、SLAベースの高品質なマネージドサービスの提供に全力で取り組んでいます。お客様のコスト削減と業績レベル向上の主導をとり、短期的にも長期的にもサポートできるようなサービス設計を行います。当社のITプロフェッショナルがお客様の技術チームの片腕となって、遠隔サポート、ヘルプデスクサポート、オフィスのセットアップ、IMACサービスをはじめ、変化するニーズに応じたサポートを提供します。', ''),
(5, 5, 'Windows 10移行', 'images/service2.jpeg', '', 'ITCSグループは、世界で有数の金融機関や中小企業に対し、世界規模でWindows 10移行を行ってきた豊富な経験を持っています。Microsoft社がWindows XPのサポートを2014年4月に打ち切ったため、Windows 7に移行せずに引き続きWindows XPのサポートを受けたい場合には膨大なコストがかかることになります。当社の専門知識をもって移行を行うことで、コスト削減、効率性向上、リスク低減、競争力強化を実現します。', ''),
(6, 6, 'IT関連規制アドバイス・サービス', 'images/service3.jpeg', '', '長期間にわたり、香港証券先物委員会（SFC）/ 香港金融管理局(HKMA)、シンガポール通貨金融庁（MAS）及び日本金融庁（FSA）は、会社法人(LC)におけるサイバーセキュリティを問題視してきました。金融機関内でのサイバーインシデント発生の報告が増加している今、この問題は同庁の最優先課題とされています。\r\n<br><br> \r\n一方で、多くの法人会社は、SFC、HKMA、MAS、および日本金融庁のIT関連規制の理解度に苦闘していると共に、具体的な成果（政策や手順を含む）が無いにも関わらず、適合しているとみなされたハイレベルな投資顧問サービスに浪費せざるを得ない事に抵抗を感じています。\r\n<br><br> \r\n2016年3月にSFにより発行された回報（2016年3月付けのSF回報）と、2016年1月にFSAにより発行された付録の改訂版には、法人会社が今後、外部の専門家からアドバイスを受ける等自社のIT構成とアクセスを厳しく見直す為の適切な措置を取るよう義務付けています。\r\n<br><br> \r\n弊社、ITCSは事の重要性を理解し、法人会社や金融機関等に適切なIT関連規制に関わるアドバイスを行ってきました。ITCSの専門家は貴社に伺い、貴社のコンピュータ環境がSFC/HKMA/MAS、そして日本金融庁の規制に沿っているかどうか、改善が必要かどうかを<a href=\"#free\" data-toggle=\'modal\' data-backdrop=\'static\' data-keyboard=\'false\'><strong>無料で査定</strong></a>致します。\r\n<br>\r\n\r\n<div class=\'modal fade\' id=\"free\">\r\n											<div class=\'modal-dialog\'>\r\n												<div class=\'modal-content\'>\r\n													<div class=\'modal-header\' style=\' background: #00768c !important; \'>\r\n														<button class=\'close\' data-dismiss=\'modal\'>&times;</button>\r\n														<h4 class=\'modal-title\' style=\' color: white !important; text-align: left !important;\'> <i class=\"fa fa-pencil-square-o fa-lg\" aria-hidden=\"true\" style=\"color: white !important;\"></i> 無料査定 </h4>\r\n													</div><!-- end of modal-header-->\r\n													<div class=\'modal-body\'>\r\n														<h5 align=\'left\' class=\'modalbody\'> お客様からのEメール受信、調査後、相談員による直接対応. </h5> </br>\r\n\r\n														<!--Form-->\r\n														<form class=\'form-horizontal\' action=\'freeassessment.php\' method=\'POST\'>\r\n														  <div class=\'form-group required\'>\r\n														    <label for=\'companyname\' class=\'col-lg-2 control-label\'>会社名</label>\r\n														    <div class=\'col-lg-10\'>\r\n														      <input type=\'text\' class=\'form-control\' id=\'companyname\' name=\'companyname\' placeholder=\'会社名\' required>\r\n														    </div>\r\n												